    ]


    // remove task

    let removeTasks = (id: number) => {
        task = task.filter((t) => {
            if (t.id !== id) {
                return true;
            }
        })
    }

    return (
        <div className="App">
            <Todolist
                title='Todo List'
                task={task}
                removeTasks={removeTasks}
            />
        </div >
    );
}

export default App;
